mapper.select:
select
  customer_id, user_id, customer_name, customer_phone, customer_email, customer_points, create_by, create_time, update_by, update_time, delete_flag
from seckill_customer

    and customer_id = #{customerId}
    and user_id = #{userId}
    and customer_name = #{customerName}
    and customer_phone = #{customerPhone}
    and customer_email = #{customerEmail}
    and customer_points = #{customerPoints}
    and create_by = #{createBy}
    and create_time = #{createTime}
    and update_by = #{updateBy}
    and update_time = #{updateTime}
    and delete_flag = #{deleteFlag}

select count(1)
from seckill_customer

    and customer_id = #{customerId}
    and user_id = #{userId}
    and customer_name = #{customerName}
    and customer_phone = #{customerPhone}
    and customer_email = #{customerEmail}
    and customer_points = #{customerPoints}
    and create_by = #{createBy}
    and create_time = #{createTime}
    and update_by = #{updateBy}
    and update_time = #{updateTime}
    and delete_flag = #{deleteFlag}

mapper.update:
update seckill_customer
user_id = #{userId}, customer_name = #{customerName}, customer_phone = #{customerPhone}, customer_email = #{customerEmail}, customer_points = #{customerPoints}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, delete_flag = #{deleteFlag},
where customer_id = #{customerId}
