mapper.select:
select
  item_id, item_name, item_code, item_price, item_store, item_is_on_sale, item_is_seckill, create_by, create_time, update_by, update_time, delete_flag
from seckill_item

    and item_id = #{itemId}
    and item_name = #{itemName}
    and item_code = #{itemCode}
    and item_price = #{itemPrice}
    and item_store = #{itemStore}
    and item_is_on_sale = #{itemIsOnSale}
    and item_is_seckill = #{itemIsSeckill}
    and create_by = #{createBy}
    and create_time = #{createTime}
    and update_by = #{updateBy}
    and update_time = #{updateTime}
    and delete_flag = #{deleteFlag}

select count(1)
from seckill_item

    and item_id = #{itemId}
    and item_name = #{itemName}
    and item_code = #{itemCode}
    and item_price = #{itemPrice}
    and item_store = #{itemStore}
    and item_is_on_sale = #{itemIsOnSale}
    and item_is_seckill = #{itemIsSeckill}
    and create_by = #{createBy}
    and create_time = #{createTime}
    and update_by = #{updateBy}
    and update_time = #{updateTime}
    and delete_flag = #{deleteFlag}

mapper.update:
update seckill_item
item_name = #{itemName}, item_code = #{itemCode}, item_price = #{itemPrice}, item_store = #{itemStore}, item_is_on_sale = #{itemIsOnSale}, item_is_seckill = #{itemIsSeckill}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, delete_flag = #{deleteFlag},
where item_id = #{itemId}
