mapper.select:
select
  refund_id, sale_id, refund_price, refund_desc, refund_status, refund_time, refund_type, create_by, create_time, update_by, update_time, delete_flag
from seckill_refund

    and refund_id = #{refundId}
    and sale_id = #{saleId}
    and refund_price = #{refundPrice}
    and refund_desc = #{refundDesc}
    and refund_status = #{refundStatus}
    and refund_time = #{refundTime}
    and refund_type = #{refundType}
    and create_by = #{createBy}
    and create_time = #{createTime}
    and update_by = #{updateBy}
    and update_time = #{updateTime}
    and delete_flag = #{deleteFlag}

select count(1)
from seckill_refund

    and refund_id = #{refundId}
    and sale_id = #{saleId}
    and refund_price = #{refundPrice}
    and refund_desc = #{refundDesc}
    and refund_status = #{refundStatus}
    and refund_time = #{refundTime}
    and refund_type = #{refundType}
    and create_by = #{createBy}
    and create_time = #{createTime}
    and update_by = #{updateBy}
    and update_time = #{updateTime}
    and delete_flag = #{deleteFlag}

mapper.update:
update seckill_refund
sale_id = #{saleId}, refund_price = #{refundPrice}, refund_desc = #{refundDesc}, refund_status = #{refundStatus}, refund_time = #{refundTime}, refund_type = #{refundType}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, delete_flag = #{deleteFlag},
where refund_id = #{refundId}
