mapper.select:
select
  sale_item_id, item_id, sale_item_price, sale_item_quantity, sale_item_status, create_by, create_time, update_by, update_time, delete_flag
from seckill_sale_item

    and sale_item_id = #{saleItemId}
    and item_id = #{itemId}
    and sale_item_price = #{saleItemPrice}
    and sale_item_quantity = #{saleItemQuantity}
    and sale_item_status = #{saleItemStatus}
    and create_by = #{createBy}
    and create_time = #{createTime}
    and update_by = #{updateBy}
    and update_time = #{updateTime}
    and delete_flag = #{deleteFlag}

select count(1)
from seckill_sale_item

    and sale_item_id = #{saleItemId}
    and item_id = #{itemId}
    and sale_item_price = #{saleItemPrice}
    and sale_item_quantity = #{saleItemQuantity}
    and sale_item_status = #{saleItemStatus}
    and create_by = #{createBy}
    and create_time = #{createTime}
    and update_by = #{updateBy}
    and update_time = #{updateTime}
    and delete_flag = #{deleteFlag}

mapper.update:
update seckill_sale_item
item_id = #{itemId}, sale_item_price = #{saleItemPrice}, sale_item_quantity = #{saleItemQuantity}, sale_item_status = #{saleItemStatus}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, delete_flag = #{deleteFlag},
where sale_item_id = #{saleItemId}
