mapper.select:
select
  sale_id, sale_sn, customer_id, seckill_id, sale_status, sale_time, sale_amount, create_by, create_time, update_by, update_time, delete_flag
from seckill_sale

    and sale_id = #{saleId}
    and sale_sn = #{saleSn}
    and customer_id = #{customerId}
    and seckill_id = #{seckillId}
    and sale_status = #{saleStatus}
    and sale_time = #{saleTime}
    and sale_amount = #{saleAmount}
    and create_by = #{createBy}
    and create_time = #{createTime}
    and update_by = #{updateBy}
    and update_time = #{updateTime}
    and delete_flag = #{deleteFlag}

select count(1)
from seckill_sale

    and sale_id = #{saleId}
    and sale_sn = #{saleSn}
    and customer_id = #{customerId}
    and seckill_id = #{seckillId}
    and sale_status = #{saleStatus}
    and sale_time = #{saleTime}
    and sale_amount = #{saleAmount}
    and create_by = #{createBy}
    and create_time = #{createTime}
    and update_by = #{updateBy}
    and update_time = #{updateTime}
    and delete_flag = #{deleteFlag}

mapper.update:
update seckill_sale
sale_sn = #{saleSn}, customer_id = #{customerId}, seckill_id = #{seckillId}, sale_status = #{saleStatus}, sale_time = #{saleTime}, sale_amount = #{saleAmount}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, delete_flag = #{deleteFlag},
where sale_id = #{saleId}
